import styled from 'styled-components';

export const Container = styled.div`

`;

export const Search = styled.div`
  position: relative;

  div {
    color: var(--gray);
    top: 11px;
    left: 10px;
    font-size: 20px;
    position: absolute;
  }

  input {
    padding: 12px 30px 14px 36px;
    border-radius: 50px;
    font-size: 1rem;
    border: none;
    outline: none; 
    background-color: var(--darkgray);
    color: var(--gray);

    &::placeholder {
      opacity: 1;
    }
  }
`;
