import styled, { css } from 'styled-components';
import { IoImageOutline } from 'react-icons/io5'
import { MdDateRange } from 'react-icons/md'
import { BiPoll } from 'react-icons/bi'
import { HiOutlineEmojiHappy } from 'react-icons/hi'
import { AiOutlineGif } from 'react-icons/ai'

export const Container = styled.div`
  width: 100%;

  div {
    display: flex;
  }
`;

export const Header = styled.div`
  padding: 15px 18px;
  border-bottom: 1px solid var(--darkgray);

  h1 {
    color: var(--white);
    font-size: 1.2rem;
  }
`;

export const WriteTweet = styled.div`
  display: flex;
  padding:  20px 10px;
`;

export const Profile = styled.div`
  img {
     height: 50px;
     width: 50px;
     border-radius: 50%;
  }
`;

export const Text = styled.div`

`;

export const IconsSection = styled.div`

`;

export const RightComponents = styled.div`
  display: flex;
  flex-direction: column;

`;

export const Icons = styled.div`
  display: flex;
  align-items: center;
  margin-left: auto;
  width: calc(600px - 90px);
`;

export const Button = styled.div`
  outline: none;
  border: none;
  color: #fff;
  width: 100px;
  text-align: center;
  padding: 10px 20px 8px 20px;
  border-radius: 20px;
  background-color: #1da1f2;
  transition: filter 300ms ease;
  cursor: pointer;
  &:hover {
    filter: brightness(1.15)
  }
`;

const iconEf = css`
  color: #1da1f2;
  font-size: 25px;
  margin-right: 25px;
  cursor: pointer;
  transition: filter 300ms ease;
  &:hover {
    filter: brightness(1.3)
  }
`;

export const IconImage  = styled(IoImageOutline)`
  ${iconEf}
`;

export const IconGif  = styled(AiOutlineGif)`
  ${iconEf}
`;

export const IconPoll  = styled(BiPoll)`
  ${iconEf}
`;

export const IconEmoji  = styled(HiOutlineEmojiHappy)`
  ${iconEf}
`;

export const IconTask  = styled(MdDateRange)`
  ${iconEf}
`;
